mapper.insert:
INSERT INTO member (
    user_id, username, nickname, password,
    birthday, email
) VALUES (
             #{userId}, #{username}, #{nickname}, #{password},
             #{birthday}, #{email}
         )

mapper.select:
select exists(
    select 1
    from member
    where user_id = #{userId}
)

select exists(
    select 1
    from member
    where nickname = #{nickname}
)

select exists(
    select 1
    from member
    where email = #{email}
)
